mapper.insert:

        SELECT IFNULL(MAX(rno),0) + 1 FROM p_reply      

	INSERT INTO p_reply
	VALUES( #{rno}, #{bno}, #{nick}, #{content}, now(), #{rno}, 0, 0 );

mapper.select:
SELECT preply
FROM p_table
WHERE pseq=#{bno}

SELECT count(rno)
FROM p_reply
WHERE rno != #{rno} and grp = #{rno}

SELECT count(rno)
FROM p_reply
WHERE rno != #{grp} and grp = #{grp}

SELECT count(rno)
FROM p_reply
WHERE bno=#{bno}
